        dropoffLocation,
        pickupTime: new Date(pickupTime),
        customerName,
        customerPhone,
        status: 'pending',
      };
      dispatch({ type: 'ADD_BOOKING', payload: newBooking });
      setPickupLocation('');
      setDropoffLocation('');
      setPickupTime('');
      setCustomerName('');
      setCustomerPhone('');
    },
    [dispatch, pickupLocation, dropoffLocation, pickupTime, customerName, customerPhone]
  );

  return (
    <form onSubmit={handleSubmit} className="bg-white p-6 rounded-lg shadow-md max-w-md mx-auto">
      <h2 className="text-2xl font-bold mb-4">Book a Ride</h2>
      <div className="mb-4">
        <label htmlFor="pickupLocation" className="block text-gray-700 font-bold mb-2">
