  const [currentPiece, setCurrentPiece] = useState<Tetromino>({ shape: T, color: '#00C4FF', pos: { x: 3, y: 0 } });
  const [nextPiece, setNextPiece] = useState<Tetromino>({ shape: I, color: '#FF6200', pos: { x: 0, y: 0 } });
